import React, { useEffect, useState } from 'react'
import Navbar from "../components/Navbar.js"
import Carousel from "../components/Carousel.js"
import Footer from "../components/Footer.js"
import Card from "../components/Card.js"

export default function Home() {
    const [foodCat, setFoodCat] = useState([])
    const [foodItem, setFoodItem] = useState([])

    useEffect(() => {
        loadData()
    }, [])

    const loadData = async () => {
        let response = await fetch("http://localhost:9000/api/getFood", {
            method: "POST",
            headers: { "Content-Type": "application/json" }
        });
        response = await response.json();
        console.log("Categories:", response[0]);
        console.log("Food Items:", response[1]);
        setFoodCat(response[0]);
        setFoodItem(response[1]);
    }

    return (
        <div>
            <Navbar />
            <Carousel />
            <div>
                {foodCat.length ? foodCat.map((data) => {
                    return (
                        <div key={data._id}>
                            <div className="fs-3 m-3">{data.categoryName}</div>
                            <hr />
                            {foodItem.length ? foodItem.filter((element) => {
                                // Return only items that match the current category
                                return element.categoryName === data.categoryName
                            }).map((filterItem) => {
                                console.log("Rendering Card for:", filterItem);  // Debugging: Check what's rendered
                                return (
                                    <div key={filterItem._id}>
                                        <Card />
                                    </div>
                                )
                            }) : ""}
                        </div>
                    )
                }) : ""}
            </div>

            <Footer />
        </div>
    )
}
