import React from 'react';
import { Link } from "react-router-dom";

export default function Card(props) {
    // Crop function to modify image path
    const crop = (msg) => {
        // Check if msg is defined and is a string
        if (typeof msg === 'string') {
            // Check if the message includes "pictures/"
            if (msg.includes("pictures/")) {
                return msg.replace(/^.*\/pictures\//, './pictures/');
            }
            // Check if the message includes "uploads/"
            if (msg.includes("uploads/")) {
                return msg.replace("uploads/", "./pictures/");
            }
        }
        // Return the original message if no conditions are met or if msg is not a string
        return msg || '';
    };

    return (
        <div>
            <div className="card mt-3 mx-3" style={{ width: "18rem" }}>
                {/* Use props.foodImg for dynamic image rendering */}
                <img className="card-img-top" src={props.foodImg} alt={`${props.foodName} image`} />
                <div className="card-body">
                    <h5 className="card-title">{props.foodName}</h5>
                    <p className="card-text">{props.foodIntro} ** {crop(props.foodImg)}</p>
                    <Link to="/" className="btn btn-primary">Go somewhere</Link>
                </div>
            </div>
        </div>
    );
}
