import React from 'react';
import { Link } from "react-router-dom";

export default function Card(props) {

    // Crop function to modify image path
    const crop = () => {
        // Check if msg is defined and is a string
        // if (typeof msg === 'string') {
        //     // Handle "uploads/" path
        //     if (msg.includes("uploads/")) {
        //         return msg.replace("uploads/", "/pictures/");
        //     }
        //     // Handle "../src/pictures/" path
        //     else if (msg.includes("../src/pictures/")) {
        //         return msg.replace("../src/pictures/", "/pictures/");
        //     }
        // }
        // Return the original message if no conditions are met or if msg is not a string
        return  '../pictures/example.png'; // Set a default image path
    };

    // Use the cropped image path for the src attribute
    const imagePath = crop(props.Img);

    return (
        <div>
            <div className="card mt-3 mx-3" style={{ width: "18rem" }}>
                {/* Use the dynamic image path */}
                <img className="card-img-top" src={imagePath} alt={`${props.foodName} foto`} />
                <div className="card-body">
                    <h5 className="card-title">{props.foodName}</h5>
                    <p className="card-text">{props.foodIntro} ** {props.foodImg}</p>
                    <Link to="/" className="btn btn-primary">Go somewhere</Link>
                </div>
            </div>
        </div>
    );
}
