import React from 'react';
import { Link } from "react-router-dom";

export default function Card(props) {
    // Crop function to create the image path
    const crop = (imgName) => {
        // Assuming images are in the public/pictures directory
        if (typeof imgName === 'string') {
            return `../pictures/${imgName}`; // Return the path to the public directory
        }
        return '../pictures/example.png'; // Default image
    };

    // Use the cropped image path for the src attribute
    const imagePath = crop(props.foodImg);

    return (
        <div>
            <div className="card mt-3 mx-3" style={{ width: "18rem" }}>
                <img className="card-img-top" src={imagePath}  />
                <div className="card-body">
                    <h5 className="card-title">{props.foodName}</h5>
                    <p className="card-text">{props.foodIntro}</p>
                    <Link to="/" className="btn btn-primary">Go somewhere</Link>
                </div>
            </div>
        </div>
    );
}
