import React from 'react';
import { Link } from "react-router-dom";
import exampleImage from '../pictures/example.png'; // Adjust path accordingly


export default function Card(props) {


    return (
        <div>
            <div className="card mt-3 mx-3" style={{ width: "18rem" }}>
                {/* Use dynamic path in an img tag instead of require */}
                <img className="card-img-top" src={require(exampleImage)} alt={`${props.foodName} foto`} />
                <div className="card-body">
                    <h5 className="card-title">{props.foodName}</h5>
                    <p className="card-text">{props.foodIntro} ** {props.foodImg}</p>
                    <Link to="/" className="btn btn-primary">Go somewhere</Link>
                </div>
            </div>
        </div>
    );
}
