import React from 'react'

export default function ViewFood(props) {

    const cropImgPath = (msg) => {
        if (typeof msg === 'string') {
            // Handle "uploads/" path
            if (msg.includes("../public/fotos/")) {
                return msg.replace("../public/fotos/", "./fotos/");
            }
        } else {
            console.warn('msg is not a valid string:', msg); // Log a warning if msg is not valid
        }
        return '../pictures/example.png'; // Set a default image path
    };

    //Ensure props.Img is passed correctly, and use it for the crop function
    const imagePath = cropImgPath(props.select.image);
    const nameI = []
    const quantityI = []

    props.select.ingredients.forEach(element => {
        nameI.push(element.name)
        quantityI.push(element.quantity)
    });
    nameI.forEach(i => console.log("naaaaam",i));
    quantityI.forEach(i => console.log("qtyyyyyy",i));


    const firstIngredient = props.select.ingredients[0];
    const { name, quantity, _id } = firstIngredient;
    console.log("Name:", name); // Output: Name: asdf
    console.log("Quantity:", quantity, "\nid: ", _id); // Output: Quantity: sdaf


    return (
        <div>
            <img style={{ height: "10rem", width: "10rem" }} src={imagePath} alt="incorrect filepath"></img>
            <div className="mb-3">
                <label for="exampleFormControlInput1" className="form-label">name</label>
                <div>{props.select.name}</div>

                <hr></hr>
            </div>
            <div className="mb-3">
                <label for="exampleFormControlTextarea1" className="form-label">category</label>
                <div>{props.select.categoryName}</div>

                <hr></hr>
            </div>
            <div className="mb-3">
                <label for="exampleFormControlTextarea1" className="form-label">Ingredients name</label>
                <div>{nameI.forEach(i => console.log(i))}</div>
                <label for="exampleFormControlTextarea1" className="form-label">Unit</label>
                <div>{quantityI.forEach(i => console.log(i))}</div>
                <hr></hr>
            </div>
            <div className="mb-3">
                <label for="exampleFormControlTextarea1" className="form-label">shortDesciption</label>
                <div>{props.select.shortDescription}</div>

                <hr></hr>
            </div>
            <div className="mb-3">
                <label for="exampleFormControlTextarea1" className="form-label">Methods</label>
                <div>{props.select.description}</div>
                <hr></hr>
            </div>
        </div>
    )
}
