import React from 'react'
import { Link } from "react-router-dom";
import charImage from './images/char.png';

export default function Card(props) {
    const crop = (msg)=>{
        if (msg.include("pictures/")){
            return msg.replace("/^.*\/pictures\//", `./pictures`);
        }
        if (msg.include("uploads/")){
            return msg.replace("uploads/", "./pictures/" )
        }
    }
    return (
        <div>
            <div className="card mt-3 mx-3" style={{ width: "18rem" }}>
                <img className="card-img-top" src={charImage} alt="no  rn" />
                <div className="card-body">
                    <h5 className="card-title">{props.foodName} {crop(props.Img)}</h5>
                    <p className="card-text">{props.foodIntro} ** {props.foodImg}</p>
                    <Link to="/" className="btn btn-primary" >Go somewhere</Link>
                </div>
            </div>
        </div>
    )
}
